import React, { useState, useEffect } from 'react';
import Chatbot from './components/Chatbot';
import api from './services/api';
import { connectSocket, disconnectSocket } from './services/socket';
import './App.css';

function App() {
    const [token, setToken] = useState(localStorage.getItem('token'));
    const [user, setUser] = useState(null);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isRegistering, setIsRegistering] = useState(false);
    const [notifications, setNotifications] = useState([]);

    useEffect(() => {
        const fetchUserAndConnect = async () => {
            const currentToken = localStorage.getItem('token');
            if (currentToken) {
                try {
                    const response = await api.get('/users/me');
                    const currentUser = response.data;
                    setUser(currentUser);
                    // Pass user ID and a callback to handle incoming messages
                    connectSocket(currentUser.id, (newMessage) => {
                         setNotifications(prev => [...prev, newMessage]);
                    });
                } catch (error) {
                    console.error("Failed to fetch user, logging out.", error);
                    handleLogout();
                }
            }
        };
        fetchUserAndConnect();

        // Cleanup on component unmount
        return () => {
            disconnectSocket();
        };
    }, [token]);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('username', email);
            formData.append('password', password);
            const response = await api.post('/users/token', formData);
            const newToken = response.data.access_token;
            localStorage.setItem('token', newToken);
            setToken(newToken);
        } catch (error) {
            console.error("Login failed:", error);
            alert("Login Failed: " + (error.response?.data?.detail || 'Please check your credentials.'));
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        try {
            await api.post('/users/register', { email, password });
            alert('Registration successful! Please log in.');
            setIsRegistering(false);
            setEmail('');
            setPassword('');
        } catch (error) {
            console.error("Registration failed:", error);
            alert("Registration Failed: " + (error.response?.data?.detail || 'An error occurred.'));
        }
    };
    
    const handleLogout = () => {
        localStorage.removeItem('token');
        setToken(null);
        setUser(null);
        disconnectSocket();
    };

    if (!token || !user) {
        return (
            <div className="auth-container">
                <div className="auth-form">
                    <h1>{isRegistering ? 'Create Account' : 'Welcome Back'}</h1>
                    <form onSubmit={isRegistering ? handleRegister : handleLogin}>
                        <input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="Email" required />
                        <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required />
                        <button type="submit">{isRegistering ? 'Register' : 'Login'}</button>
                    </form>
                    <button onClick={() => setIsRegistering(!isRegistering)} className="toggle-button">
                        {isRegistering ? 'Already have an account? Login' : 'Need an account? Register'}
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="app-container">
            <header className="app-header">
                <h2>AI Credit Underwriter</h2>
                <div className="user-info">
                    <span>{user.email} {user.is_admin && '(Admin)'}</span>
                    <button onClick={handleLogout}>Logout</button>
                </div>
            </header>
            <main className="main-content">
                <Chatbot user={user} />
                <div className="notifications-panel">
                    <h3>Real-time Notifications</h3>
                    <ul className="notification-list">
                        {notifications.map((note, index) => <li key={index}>{note}</li>)}
                         {notifications.length === 0 && <li>No new notifications.</li>}
                    </ul>
                </div>
            </main>
        </div>
    );
}

export default App;
